def get_fields_for_type(data_type):
    """
    Returns the fields for a given data type.
    If the data type is not found, returns an empty list.
    """
    if data_type in reliefweb_fields:
        return reliefweb_fields[data_type]
    else:
        return []  # Return empty list if data type is unknown
